import "../CSS/Todo.css";
import { DataContext } from "../DataContext";
//import { useContext, useEffect } from "react";
//import { useNavigate } from "react-router-dom";
//import React, { Component } from "react";
import React, { useState, useContext } from "react";

function Todo() {
  const [userInput, setUserInput] = useState("");
  const [list, setList] = useState([]);

  const updateInput = (value) => {
    setUserInput(value);
  };

  const addItem = () => {
    if (userInput.trim() !== "") {
      const newItem = {
        id: Math.random(),
        value: userInput.trim(),
        done: false,
      };
      setList([...list, newItem]);
      setUserInput("");
    }
  };

  const deleteItem = (key) => {
    const updatedList = list.filter((item) => item.id !== key);
    setList(updatedList);
  };

  const markAsDone = (index) => {
    const updatedList = [...list];
    updatedList[index].done = !updatedList[index].done;
    setList(updatedList);
  };

  const editItem = (index) => {
    const editedTodo = prompt('Edit the task:', list[index].value);
    if (editedTodo !== null && editedTodo.trim() !== '') {
      const updatedList = [...list];
      updatedList[index].value = editedTodo.trim();
      setList(updatedList);
    }
  };

  const clearTasks = () => {
    setList([]);
  };

  return (
    <div>
      <div className="bodyTask">
        <div className="">
          <input
            className="addTask"
            type="text"
            placeholder="add task . . ."
            value={userInput}
            onChange={(e) => updateInput(e.target.value)}
          />
          <div>
              <input
                className="addButton"
                type="button"
                value="ADD"
                onClick={addItem}
              />
            <div className="clearButton">
              <input
                type="button"
                value="CLEAR"
                onClick={clearTasks}
              />
            </div>
          </div>
        </div>
      </div>

      <ul>
        {list.map((item, index) => (
          <li key={index}>
            <span style={{ textDecoration: item.done ? 'line-through' : 'none' }}>
              {item.value}
            </span>
            <button onClick={() => markAsDone(index)}>Done</button>
            <button onClick={() => deleteItem(item.id)}>Delete</button>
            <button onClick={() => editItem(index)}>Edit</button> 
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Todo;
